import 'dart:html';
import 'package:flutter/material.dart';
import '/pages/detail.dart';

class HomePage extends StatefulWidget {
  const HomePage({Key? key}) : super(key: key);
  @override
  _HomePageState createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text("ความรู้เกี่ยวกับคอมพิวเตอร์"),
      ),
      body: ListView(
        padding: const EdgeInsets.all(20),
        children: [
          myBox(
              "หวัดไม่ดี",
              "by มะพร้าว",
              "data:image/png;base64,"),
          SizedBox(
            height: 30,
          ),
          myBox(
              "กีตาร์",
              "เป็นนักกีตาร์",
              "data:image/jpeg;base64,"),
          SizedBox(height: 30),
          myBox(
              "มะพร้าวน้ำหอม",
              "https://www.youtube.com/watch?v=OYp9wIQ9aqs",
              "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRpLT41F68Nlc0zzLnJZP2cOI9VKRpQ-DOzqA&usqp=CAU"),
        ],
      ),
    );
  }

  Widget myBox(String title, String subTitle, String imgUrl) {
    return Container(
      padding: EdgeInsets.all(20),
      height: 250,
      decoration: BoxDecoration(
        borderRadius: BorderRadius.circular(15),
        image: DecorationImage(
          image: NetworkImage(imgUrl),
          fit: BoxFit.cover,
          colorFilter:
              ColorFilter.mode(Colors.black.withOpacity(0.5), BlendMode.darken),
        ),
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.start,
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(
            title,
            style: TextStyle(
                fontSize: 25,
                color: Colors.yellow,
                fontWeight: FontWeight.bold),
          ),
          SizedBox(
            height: 20,
          ),
          Text(
            subTitle,
            style: TextStyle(fontSize: 15, color: Colors.yellow[100]),
          ),
          SizedBox(height: 20),
          TextButton(
              onPressed: () {
                print("Next Page >>>");
                Navigator.push(context,
                    MaterialPageRoute(builder: (context) => DetailPage()));
              },
              child: Text("Next Page >>>"))
        ],
      ),
    );
  }
}
